import {useState, useEffect, useRef} from 'react';
import happi from '../api/happi';


export interface ISignUpData {
    album: string;
    api_album: "https://api.happi.dev/v1/music/artists/274635/albums/545418"
    api_albums: "https://api.happi.dev/v1/music/artists/274635/albums"
    api_artist: "https://api.happi.dev/v1/music/artists/274635"
    api_lyrics: "https://api.happi.dev/v1/music/artists/274635/albums/545418/tracks/8107931/lyrics"
    api_track: "https://api.happi.dev/v1/music/artists/274635/albums/545418/tracks/8107931"
    api_tracks: "https://api.happi.dev/v1/music/artists/274635/albums/545418/tracks"
    artist: "Basics Of Ballet"
    bmp: 86
    cover: "https://api.happi.dev/v1/music/cover/545418"
    haslyrics: false
    id_album: 545418
    id_artist: 274635
    id_track: 8107931
    track: "Twist for the Last Time"
  }
export default () => {

    const [results,setResults]:any = useState({
        firstName: null,
        emailAddress: null,
    });
    const [errorMsg,setError]:any = useState('');
    const mountedRef:any = useRef(true);

    const getTracksByName = async searchTerm => {
        try{
            const response = await happi.get('/music', {
                params:{
                    q:searchTerm,
                    limit:50,
                    apikey:'42405dFzin2GNUB68DBOOniGJLng7JMKBTGzosIyjGiEDUjuWj1GNv8p',
                    type:'track'
                }
            });

            setResults(response.data.result);
        }
        catch(err){

          setError("somthing went wrong")
        }

    };
    useEffect(()=>{
        getTracksByName('sia');
        return () => {
            mountedRef.current = false;   // clean up function
          }
    },[])

    return [results,getTracksByName,errorMsg];
};
